mapper.insert:
INSERT INTO tb_tomato_order_info (
        tmt_pp_cd
    ,   tmt_pp_etc
    ,   tmt_size_json
    ,   tmt_total_amt
    ,   orderer_idx
    ,   receiver_idx
    ,   ship_memo
    ,   cash_recpt_num
    ,   deposit_yn
    ,   deposit_dt
    ,   sales_price
    ,   note
    ,   regist_dt
    ,   update_dt
) VALUE (
        #{tmtPpCd}
    ,   #{tmtPpEct}
    ,   #{tmtSizeJson}
    ,   #{tmtTotalAmt}
    ,   #{ordererIdx}
    ,   #{receiverIdx}
    ,   #{shipMemo}
    ,   #{cashRecptNum}

    ,   'N'


    ,   'Y'


    ,   null


    ,   #{depositDt}

    ,   #{salesPrice}
    ,   #{note}
    ,   CURRENT_TIMESTAMP
    ,   #{updateDt}
)

INSERT INTO tb_orderer_info (
        orderer_nm
    ,   orderer_mobile
    ,   orderer_adr
    ,   orderer_detail_adr
    ,   orderer_zipcode
    ,   regist_dt
    ,   update_dt
    ) VALUES (
        #{ordererNm}
    ,   #{ordererMobile}
    ,   #{ordererAdr}
    ,   #{ordererDetailAdr}
    ,   #{ordererZipcode}
    ,   #{registDt}
    ,   #{updateDt}
    )

INSERT INTO tb_receiver_info (
    receiver_nm
,   receiver_mobile
,   receiver_adr
,   receiver_detail_adr
,   receiver_zipcode
,   regist_dt
,   update_dt
) VALUES (
    #{receiverNm}
,   #{receiverMobile}
,   #{receiverAdr}
,   #{receiverDetailAdr}
,   #{receiverZipcode}
,   #{registDt}
,   #{updateDt}
)

mapper.select:
SELECT orderer_idx FROM tb_orderer_info WHERE 1=1
and orderer_nm = '신재희'
and orderer_nm = #{ordererNm}
and orderer_mobile = '010-5392-4329'
and orderer_mobile = #{ordererMobile}

SELECT
    receiver_idx
FROM tb_receiver_info
WHERE 1=1

    and receiver_nm = #{receiverNm}


    and receiver_mobile = #{receiverMobile}
